import React, { useEffect } from "react";

const SelectMenu = () => {
  const className = "bg-red-600 hover:bg-green-500";
  useEffect(() => {
    const langSelBox = document.querySelector(".lang-sel");
    console.log(langSelBox?.value);
  }, []);

  return (
    <select
      name=""
      id=""
      value="English"
      className="lang-sel border outline-none p-3 appearance-none"
    >
      <option value="English" className={className}>
        English
      </option>
      <option value="Spanish" className={className} selected>
        Spanish
      </option>
    </select>
  );
};

export default SelectMenu;
