import React, { FC } from "react";

type loginInput = {
  inputType: string;
  id: string;
  labelContent: string;
  spanContent?: string;
};

const LoginInput: FC<loginInput> = ({
  inputType,
  id,
  labelContent,
  spanContent,
}) => {
  const span = `<span>${spanContent}</span>`;
  let spanState = false;
  const inputControl = (e: any) => {
    if (e.target.value === "") {
      e.target.classList.add("border-red-500", "border-b-2");
    } else {
      console.log("b");
    }
    console.log(e.target);
  };
  return (
    <div className="flex items-center relative w-full mb-4 rounded overflow-hidden border">
      <input
        type={inputType}
        name=""
        id={id}
        onBlur={(e) => inputControl(e)}
        className="w-full px-5 pt-6 peer focus:outline-0 focus:border-b-2 focus:border-red-500"
      />

      <label
        htmlFor={id}
        className="absolute left-5 peer-focus:text-[11px] peer-focus:top-2 peer-invalid:text-[11px] peer-invalid:top-2 duration-100"
      >
        {labelContent}
      </label>
    </div>
  );
};

export default LoginInput;
