import React, { FC, useContext } from "react";
import { GSLangContext } from "../../../contexts/GetStartedLangContext";

type img_video = {
  sectionNum?: number;
  img1src?: string;
  img2src?: string;
  img3src?: string;
  videosrc?: string;
  imgSide?: "left" | "right";
  videoClass?: string;
  img2Class?: string;
  img3Class?: string;
};

const MainItem: FC<img_video> = ({
  sectionNum,
  img1src,
  img2src,
  img3src,
  videosrc,
  imgSide,
  videoClass,
  img2Class,
  img3Class,
}) => {
  const context = useContext(GSLangContext).main;
  const main = context + `.s${sectionNum}`;

  return (
    /* px-[23rem] */
    <div
      className={`flex ${
        imgSide === "right" ? "flex-row-reverse" : "flex-row"
      }`}
    >
      <div className="relative">
        <img src={img1src} alt="" />
        {videosrc ? <video src={videosrc} className={videoClass}></video> : ""}
        {img2src ? <img src={img2src} alt="" className={img2Class} /> : ""}
        {img3src ? <img src={img3src} alt="" className={img3Class} /> : ""}
      </div>
      <div className="w-96">
        <h1>{main.s1.H1}</h1>
        <h2>{main.s1.H2}</h2>
      </div>
    </div>
  );
};

export default MainItem;
