import React from "react";

const InputMail = () => {
  return (
    <div className="border p-3 relative">
      <input type="email" id="headermail" className="border peer p-3" />
      <label
        htmlFor="headermail"
        className="absolute left-5 top-6 peer-focus:-top-1 peer-focus:left-7 peer-invalid:-top-1 peer-invalid:left-7 bg-white px-1 transition-all duration-100 border border-black"
      >
        mail
      </label>
    </div>
  );
};

export default InputMail;
