import React, { FC, useContext } from "react";
import { GSLangContext } from "../../../contexts/GetStartedLangContext";

type img_video = {
  img1src?: string;
  img2src?: string;
  img3src?: string;
  videosrc?: string;
  imgSide?: "left" | "right";
  videoClass?: string;
};

const MainItem: FC<img_video> = ({
  img1src,
  img2src,
  img3src,
  videosrc,
  imgSide,
  videoClass,
}) => {
  const main = useContext(GSLangContext).main;
  const imgHTML = `
    
  `;
  return (
    /* px-[23rem] */
    <div>
      <div className="relative">
        <img src={img1src} alt="" />
        {videosrc ? <video src={videosrc} className={videoClass}></video> : ""}
      </div>
      <div className="w-96">
        <h1>{main.s1.H1}</h1>
        <h2>{main.s1.H2}</h2>
      </div>
    </div>
  );
};

export default MainItem;
