import React, { FC, useContext } from "react";

type img_video = {
  sectionH1: string;
  sectionH2: string;
  img1src: string;
  img2src?: string;
  img3src?: string;
  videosrc?: string;
  imgSide: "left" | "right";
  videoClass?: string;
  img2Class?: string;
  img3Class?: string;
};

const MainItem: FC<img_video> = ({
  sectionH1,
  sectionH2,
  img1src,
  img2src,
  img3src,
  videosrc,
  imgSide,
  videoClass,
  img2Class,
  img3Class,
}) => {
  return (
    /* px-[23rem] */
    <div
      className={`flex ${
        imgSide === "right" ? "flex-row-reverse" : "flex-row"
      } px-[22.75rem] justify-between items-center`}
    >
      <div className="relative w-[36.3rem] h-[27.2rem]">
        <img src={img1src} alt="" className="h-full w-autofull" />
        {videosrc ? (
          <video src={videosrc} className={videoClass} autoPlay muted></video>
        ) : (
          ""
        )}
        {img2src ? <img src={img2src} alt="" className={img2Class} /> : ""}
        {img3src ? <img src={img3src} alt="" className={img3Class} /> : ""}
      </div>
      <div className="w-[36.3rem]">
        <h1 className="text-5xl font-bold">{sectionH1}</h1>
        <h2 className="text-2xl mt-4">{sectionH2}</h2>
      </div>
    </div>
  );
};

export default MainItem;
