import React, { useContext } from "react";
import { GSLangContext } from "../../contexts/GetStartedLangContext";

const EmailForm = () => {
  const form = useContext(GSLangContext).english.header.form;

  return (
    <form>
      <h3>{form.Title}</h3>
      <div className="flex justify-center h-[4.375rem]">
        <div className="input-box relative w-8/12 text-black px-[0.675rem] pt-[0.675rem] bg-white">
          <input
            type="email"
            name=""
            id="gsEmailBox"
            className="w-full h-full focus:outline-none peer"
          />
          <label
            htmlFor="gsEmailBox"
            className="absolute left-[0.675rem] top-7 text-[#8C8C8C] duration-100 peer-focus:top-2 peer-focus:text-[0.8125rem] peer-focus:font-medium peer-invalid:top-2 peer-invalid:text-[0.8125rem] peer-invalid:font-medium"
          >
            {/* left-[0.675rem] text-[0.8125rem] font-medium text-[#8C8C8C] */}
            {form.inputLabel}
          </label>
        </div>
        <button>{form.btnTitle}</button>
      </div>
    </form>
  );
};

export default EmailForm;
