import React, { useEffect } from "react";

const SelectLang = () => {
  const className = "bg-gray-400 text-white font-medium text-center";
  const langSelBox = document.querySelector("#langSel");

  return (
    <div className="relative langSelect border inline-flex justify-between items-center mr-8 rounded">
      <select
        name=""
        id="langSel"
        defaultValue="English"
        className="outline-none appearance-none w-100 py-2 px-6 text-center text-sm focus:outline-blue-600 focus:outline-offset-0 rounded-"
      >
        <option value="English" className={className}>
          English
        </option>
        <option value="Spanish" className={className}>
          Spanish
        </option>
      </select>
    </div>
  );
};

export default SelectLang;
