import React, { FC, useContext } from "react";

type img_video = {
  sectionH1: string;
  sectionH2: string;
  img1src: string;
  img2src?: string;
  img3src?: string;
  videosrc?: string;
  imgSide: "left" | "right";
  videoClass?: string;
  img2Class?: string;
  img3Class?: string;
  miniBoxH1?: string;
  miniBoxH2?: string;
};

const MainItem: FC<img_video> = ({
  sectionH1,
  sectionH2,
  img1src,
  img2src,
  img3src,
  videosrc,
  imgSide,
  videoClass,
  img2Class,
  img3Class,
  miniBoxH1,
  miniBoxH2,
}) => {
  return (
    /* px-[23rem] */
    <div
      className={`flex ${
        imgSide === "right" ? "flex-row-reverse" : "flex-row"
      } px-[22.75rem] justify-between items-center`}
    >
      <div className="relative min-w-[36.3rem] h-[27.2rem]">
        <img src={img1src} alt="" className="h-full" />
        {videosrc ? (
          <video src={videosrc} className={videoClass} autoPlay muted></video>
        ) : (
          ""
        )}
        {img2src ? (
          <div className="phn-download flex justify-between items-center border border-red-600">
            <img src={img2src} alt="" className={img2Class} />
            <div className="flex flex-col">
              <h1>{miniBoxH1}</h1>
              <h2>{miniBoxH2}</h2>
            </div>
            <img src={img3src} alt="" className={img3Class} />
          </div>
        ) : (
          ""
        )}
      </div>
      <div className="w-[36.3rem]">
        <h1 className="text-5xl font-bold">{sectionH1}</h1>
        <h2 className="text-2xl mt-4">{sectionH2}</h2>
      </div>
    </div>
  );
};

export default MainItem;
