import React, { useState, useEffect, createContext } from "react";
import "./App.css";
import GetStarted from "./pages/GetStarted";
import Login from "./pages/Login";
import { Routes, Route, Link, NavLink, useNavigate } from "react-router-dom";
import GSLangContProv, {
  getStartedPageLang,
} from "./contexts/GetStartedLangContext";
import { loginPageLang } from "./contexts/LoginLangContext";

function App() {
  const [isLang, setIsLang] = useState("English");
  const [GSLang, setGSLang] = useState(getStartedPageLang.english);
  const [loginLang, setLoginLang] = useState(loginPageLang.english);
  const navigate = useNavigate();

  const isLangContext = createContext({
    isLang,
    setIsLang,
    GSLang,
    setGSLang,
    loginLang,
    setLoginLang,
  });
  let langData = {
    isLang,
    setIsLang,
    GSLang,
    setGSLang,
    loginLang,
    setLoginLang,
  };

  /*  useEffect(() => {
    navigate("/nl-en");
  }, []); */

  return (
    <div>
      <isLangContext.Provider value={langData}>
        <Routes>
          <Route
            path={GSLang.link}
            element={
              <GetStarted
                isLang={isLang}
                setIsLang={setIsLang}
                lang={langData.GSLang}
                setLang={setGSLang}
              />
            }
          />
          <Route
            path="/login"
            element={
              <Login
                isLang={isLang}
                setIsLang={setIsLang}
                lang={loginLang}
                setLang={setLoginLang}
              />
            }
          />
        </Routes>
      </isLangContext.Provider>
    </div>
  );
}

export default App;
