import React, { useState, useEffect, createContext } from "react";
import "./App.css";
import GetStarted from "./pages/GetStarted";
import { Routes, Route, Link, NavLink, useNavigate } from "react-router-dom";
import GSLangContProv, {
  getStartedPageLang,
} from "./contexts/GetStartedLangContext";

function App() {
  const [isLang, setIsLang] = useState("English");
  const [lang, setLang] = useState(getStartedPageLang.english);
  const navigate = useNavigate();
  const GSLangContext = createContext(getStartedPageLang.english);

  useEffect(() => {
    navigate("/nl-en");
  }, []);

  return (
    <GSLangContext.Provider value={lang}>
      <Routes>
        <Route
          path={lang.link}
          element={
            <GetStarted
              isLang={isLang}
              setIsLang={setIsLang}
              lang={lang}
              setLang={setLang}
            />
          }
        />
      </Routes>
    </GSLangContext.Provider>
  );
}

export default App;
