import React, { useContext } from "react";
import { GSLangContext } from "../../../contexts/GetStartedLangContext";
import FAQItem from "./FAQItem";

const FAQ = () => {
  const faq = useContext(GSLangContext).faq.questions;
  return (
    <div>
      {faq.map((item) => (
        <FAQItem
          ques={item.ques}
          ans1={item.ans.p1}
          ans2={item.ans.p2}
          ansA={item.ans.link}
        />
      ))}
      {/* <FAQItem ques={faq[0].ques} /> */}
    </div>
  );
};

export default FAQ;
