import React, { useContext } from "react";
import { GSLangContext } from "../../contexts/GetStartedLangContext";

const Header = () => {
  const header = useContext(GSLangContext).english.header;

  return (
    <header className="relative w-full h-737 overflow-hidden">
      <div className="title-email text-white z-20">
        <h1 className="text-white">{header.H1}</h1>
        <h2>{header.H2}</h2>
        <form>
          <h3>{header.form.Title}</h3>
        </form>
      </div>

      <div className="img-box h-full w-full absolute top-0">
        <div className="bg-gradient-to-b from-black via-transparent to-black w-full h-full"></div>
        <img
          src="https://assets.nflxext.com/ffe/siteui/vlv3/a43711df-c428-4f88-8bb3-b2ac5f20608f/fa281d8e-aecc-47e8-81aa-caa5889085b1/NL-en-20230227-popsignuptwoweeks-perspective_alpha_website_large.jpg"
          alt=""
          className="h-full w-full object-cover"
        />
      </div>
    </header>
  );
};

export default Header;
