import React, { FC } from "react";

type loginInput = {
  inputType: string;
  id: string;
  labelContent: string;
};

const LoginInput: FC<loginInput> = ({ inputType, id, labelContent }) => {
  return (
    <div className="flex items-center relative w-full mb-5">
      <input
        type={inputType}
        name=""
        id={id}
        className="w-full px-5 pt-4 border peer"
      />
      <label
        htmlFor={id}
        className="absolute left-5 peer-focus:text-[11px] peer-invalid:text-[11px] peer-invalid:top-0"
      >
        {labelContent}
      </label>
    </div>
  );
};

export default LoginInput;
